from collections import defaultdict
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        cnt=0
        ar=defaultdict(int)
        for num in nums:
            nar=defaultdict(int)
            nar[num]+=1
            for res in ar:
                new_and=res & num
                nar[new_and]+=ar[res]
            if k in nar:
                cnt+=nar[k]
            ar=nar
        return cnt
