class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        @cache
        def solve(i, v):
            if i == len(nums):
                return int(v == k)
            if v & k != k:
                return 0
            if v == k:
                return 1 + solve(i + 1, v & nums[i])
            return solve(i + 1, v & nums[i])
        return sum(solve(i + 1, nums[i]) for i in range(len(nums)))
