class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
           int n = nums.size();
        long long totalCount = 0;

        unordered_map<long long, long long> prevResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currResults;

            if (nums[i] == k) {
                ++totalCount;
            }
            currResults[nums[i]] = 1;

            for (auto& entry : prevResults) {
                long long val = entry.first;
                long long freq = entry.second;

                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    totalCount += freq;
                }
                currResults[newAndResult] += freq;
            }

            prevResults = currResults;
        }

        return totalCount;
    }
};
