class Solution {
public:
    long long countSubarrays(vector<int>& nums, int t) {

	unordered_map<long long, long long>prev;
	long long ans = 0;

	for (long long i = 0; i < nums.size(); ++i)
	{
		unordered_map<long long, long long>curr;
		if (nums[i] == t)ans++;
		curr[nums[i]] = 1;
		for (auto it : prev) {
			long long temp = it.first & nums[i];
			if (temp == t) {
				ans += it.second;
			}
			curr[temp] += it.second;
		}
		prev = curr;
	}
	return ans;

    }
};
