class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function countSubarrays($nums, $k) {
        $n = count($nums);
        $count = 0;
        $prevResults = [];

        for ($i = 0; $i < $n; ++$i) {
            $currResults = [];

            if ($nums[$i] == $k) {
                ++$count;
            }
            $currResults[$nums[$i]] = 1;

            foreach ($prevResults as $val => $freq) {
                $newAndResult = $val & $nums[$i];
                if ($newAndResult == $k) {
                    $count += $freq;
                }
                if (!isset($currResults[$newAndResult])) {
                    $currResults[$newAndResult] = 0;
                }
                $currResults[$newAndResult] += $freq;
            }

            $prevResults = $currResults;
        }

        return $count;
    }
}
