#define ll long long 

class Solution {
public:

    long long countSubarrays(vector<int>& a, int k) {
        ll n = a.size();
        ll ans = 0;
        unordered_map<ll, ll> mp;
        unordered_map<ll, ll> up; 
        for (ll i = 0; i < n; i++) {
            up.clear();
            up[a[i]] = 1;
            if (a[i] == k) ans++;
            for (auto [x, y] : mp) {
                ll cnt = x ;
                cnt=cnt&a[i];
                if (cnt == k)  ans =ans+ y;

                up[cnt] = up[cnt]+ y;
            }
           unordered_map<ll,ll> yy=up;
            mp=yy;
        }
        return ans;
    }
};
