class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n=nums.size();
        unordered_map<long long,long long> mp;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long> mp1;
            if(nums[i]==k){
                ans++;
            }
            mp1[nums[i]]=1;
            for(auto& [it1,it2]:mp){
                long long res=it1 & nums[i];
                if(res==k){
                    ans+=it2;
                }
                mp1[res]+=it2;
            }
            mp=mp1;
        }
        return ans;
    }
};
