class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0L;
        Map<Long, Long> map = new HashMap<>();
        for(int i =0;i<n;i++){
            Map<Long, Long> curr = new HashMap<>();
            if(nums[i] == k)count++;
            curr.put((long)nums[i],1L);
            for(HashMap.Entry<Long, Long> en : map.entrySet()){
                long data = en.getKey();
                long fr = en.getValue();
                long newA = data & nums[i];
                if(newA == k)count+= fr;
                curr.put(newA, curr.getOrDefault(newA, 0L)+fr);
            }
            map = curr;
        }
        return count;
    }
}
