class Solution {
public:
    long long countSubarrays(vector<int>& nums, int tar) {
        int n = nums.size();
        long long tot = 0;
        unordered_map<long long, long long> prev;
        for (int i=0;i<n;i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i]==tar) {
                tot++;
            }
            curr[nums[i]] = 1;
            for (auto it : prev) {
                int val=it.first;
                int freq=it.second;
                long long nw = val & nums[i];
                if (nw == tar) {
                    tot += freq;
                }
                curr[nw] += freq;
            }

            prev = curr;
        }
        return tot;
    }
};
