class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long c=0;
        map<long long,long long> mpl;
        for(int i=0;i<n;i++){
            map<long long,long long> mp;
            if(nums[i]==k){
                c++;
            }
            mp[nums[i]]=1;
            for(auto [item,no]:mpl){
                long long ans=item & nums[i];
                if(ans==k){
                    c+=no;
                }
                mp[ans]+=no;
            }
            mpl=mp;


        }
        return c;

    }
};
