class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<int, int>mp;
        for (int i = 0; i<nums.size(); i++) {
            int count = -1;  
            unordered_map<int, int>mp1;
            mp1[nums[i]] = 1;
            if(nums[i] == k){
                ans++;
            }
            for(auto it : mp){
                if((nums[i]&it.first) == k){
                    ans += it.second;
                }
                mp1[(nums[i]&it.first)] += it.second;
            }
            mp = mp1;
        }
        return ans;
    }
};
