class Solution {
public:
    // dp[i][cur] = dp[i+1][cur&nums[i]]
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<int, int> m;
        for(int i = 0;i < n;i++) {
            int x = nums[i];
            unordered_map<int, int> m1;
            m1[x]++;
            for(auto [j,v]:m) {
                if((j&x)>=k) {
                    m1[j&x]+=v;
                }
            }
            if(m1.count(k))
            ans += m1[k];
            swap(m1,m);
        }
        return ans;
    }
};
