class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
            int n = nums.size(); // Correct variable name and access the size of 'nums'
        long long tot = 0;
        unordered_map<long long, long long> mp1;

        for (int index = 0; index < n; ++index) {
            unordered_map<long long, long long> mp2;
            long long tar = k; // Correctly define 'target' (assuming 'k' is the target value)

            if (nums[index] == tar) {
                ++tot;
            }
            mp2[nums[index]] = 1;

            for (auto& [value, frequency] : mp1) {
                long long newAndResult = value & nums[index]; // Bitwise AND between value and                                                                                                        //nums[index]
                if (newAndResult == tar) {
                    tot += frequency;
                }
                mp2[newAndResult] += frequency;
            }

            mp1 = mp2;
        }

        return tot;

    }
};
