#define ll long long
class Solution {    
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        ll ans = 0;
        unordered_map<ll, ll> mp1;
        for (int i = 0; i < n; i++) {
            unordered_map<ll,ll> mp2; 
            if (v[i] == k) {
                ans++;
            }
            mp2[v[i]] = 1;
            for (auto &j : mp1) {
                ll res = j.first & v[i];
                if (res == k) {
                    ans += j.second;
                }
                mp2[res] += j.second;
            }
            mp1 = mp2;
        }
        return ans;
    }
};
