typedef long long ll;
#define ump unordered_map
class Solution {
    private:
    void helper(ump<ll, ll> &v1,ump<ll, ll> &v2,int curr, int k, ll &cnt)
    {
        for (auto &[val, freq] : v1)
        {
            ll temp = val & curr;
            if (temp == k) cnt += freq;
            v2[temp] += freq;
        }
    }

public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll cnt = 0;
        ump<ll, ll> v1;
        for (int i = 0; i < n; ++i)
        {
            ump<ll, ll> v2;
            if (nums[i] == k) ++cnt;
            v2[nums[i]] = 1;
            helper(v1, v2, nums[i], k, cnt);
            v1 = v2;
        }

        return cnt;
    }
};
