class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cV = 0;
        int lenV = nums.size();
        unordered_map<long long, long long> pL;
        for (int i = 0; i < lenV; ++i) {
            unordered_map<long long, long long> cL; 
            if (nums[i] == k) {
                cV++;
            }
            cL[nums[i]] = 1;
            for (auto& [cal, fV] : pL) {
                long long rV = cal & nums[i];
                if (rV == k) {
                    cV += fV;
                }
                cL[rV] += fV;
            }
            pL = cL;
        }        
        return cV;
    }
};
