class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int arraySize = array.size();
        long long totalSubarrays = 0;
        unordered_map<long long, long long> previousAndResults;

        for (int currentIndex = 0; currentIndex < arraySize; ++currentIndex) {
            unordered_map<long long, long long> currentAndResults;

            if (array[currentIndex] == target) {
                ++totalSubarrays;
            }

            currentAndResults[array[currentIndex]] = 1;

            for (auto& [previousValue, previousFrequency] : previousAndResults) {
                long long newAndResult = previousValue & array[currentIndex];

                if (newAndResult == target) {
                    totalSubarrays += previousFrequency;
                }

                currentAndResults[newAndResult] += previousFrequency;
            }

            previousAndResults = currentAndResults;
        }

        return totalSubarrays;
    }
};
