class Solution {
    public long countSubarrays(int[] nums, int k) {
        Map<Integer, Long> all = new HashMap<>();
        Map<Integer, Long> end = new HashMap<>();
        for (int num : nums) {
            Map<Integer, Long> tmp = new HashMap<>();
            all.merge(num, 1L, Long::sum);
            tmp.merge(num, 1L, Long::sum);
            for (int n : end.keySet()) {
                int t = n;
                n &= num;
                all.merge(n, end.get(t), Long::sum);
                tmp.merge(n, end.get(t), Long::sum);
            }
            end = tmp;
        }
        return all.getOrDefault(k,0L);
    }
}
