class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long c = 0;

        unordered_map<long long, long long> pRes;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cRes;

            if (a[i] == k) {
                ++c;
            }
            cRes[a[i]] = 1;

            for (auto& [v, f] : pRes) {
                long long newAnd = v & a[i];
                if (newAnd == k) {
                    c += f;
                }
                cRes[newAnd] += f;
            }

            pRes = cRes;
        }

        return c;
    }
};
