class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        map<ll, ll> mp1;
        for(int i = 0; i < nums.size(); i++) {
            map<ll, ll> mp2; 
            ans += nums[i] == k;
            mp2[nums[i]] = 1;
            for(auto &it: mp1) {
                ll x = it.first & nums[i];
                if(x == k) {
                    ans += it.second;
                }
                mp2[x] += it.second;
            }
            mp1 = mp2;
        }
        return ans;
    }
};
