class Solution {
    public long countSubarrays(int[] nums, int k) {
       int n = nums.length;
        long res = 0;
        Map<Long, Long> s = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> c = new HashMap<>();

            if (nums[i] == k) {
                ++res;
            }

            c.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : s.entrySet()) {
                long value = entry.getKey();
                long f = entry.getValue();

                long nd = value & nums[i];

                if (nd == k) {
                    res += f;
                }

                c.put(nd, c.getOrDefault(nd, 0L) + f);
            }

            s = c;
        }

        return res;
    }
}
