class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int i,j,n=nums.size();
        long long c=0;
        unordered_map<long long,long long> mp1;
        for(i=0;i<n;i++)
        {
            unordered_map<long long,long long> mp2;
            if(nums[i]==k)
                c++;
            mp2[nums[i]]=1;
            for(auto x:mp1)
            {
                long long p=x.first & nums[i];
                if(p==k)
                    c+=(long long)x.second;
                mp2[p]+=x.second;
            }
            mp1=mp2;
        }
        return c;
    }
};
