class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans=0;
        unordered_map<long long, long long> mp;       
        int first=0;
        int second=0;
        while(first<n)
        {
            unordered_map<long long, long long> mp2;
            // while(second<=first){}
            if(nums[first]==k)ans++;
            mp2[nums[first]]=1;
            for(auto i:mp)
            {
                if((i.first&nums[first])==k)ans+=i.second;
                mp2[(i.first&nums[first])]+=i.second;
                // mp[(i.first&nums[first])]+=i.second;
            }
            mp.clear();
            for(auto i:mp2)mp[i.first]+=i.second;
            first++;
        }
        return ans;

    }
};
