class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size() ,res = 0;
int ans=0;
for(int i=0;i<n;i++){
    if(nums[i]&i){
        ans++;
    }
    else continue;
}
ans=0;

        unordered_map<long long, long long> br;
for(int i=0;i<n;i++){
    if(nums[i]&i){
        ans++;
    }
    else continue;
}
ans=max(ans,nums[n-1]);
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 

            if (nums[i] == k) {
                res++;
            }
            cr[nums[i]] = 1;

            for (auto& b : br) {
                long long tmp = b.first & nums[i];
                if (tmp == k)  res += b.second;

                cr[tmp] += b.second;
            }

            br = cr;
        }

        return res;
    }
};
