class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll c=0;
        map<ll,ll> m;
        set<int> s;
        for(auto e:nums)
            s.insert(e);
        for(int i=0;i<n;i++){
            map<ll,ll> m1;
            m1[nums[i]]=1;
            if(nums[i]==k)
                c++;

            for(auto p:m){

                ll res=(p.first&nums[i]);
                if((p.first&nums[i])==k)
                    c+=p.second;

                m1[res]+=p.second;
            }

            m=m1;
        }

        return c;
    }
};
