class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int N = nums.size();
        using LL = long long;
        vector<unordered_map<LL, LL>> cnts(N);
        LL tot = 0;
        if (nums[0] == k) tot++;
        cnts[0][nums[0]]++;
        for(int i = 1; i < N; i++) {
            for(auto &e : cnts[i - 1]) {
                cnts[i][e.first & nums[i]] += e.second;
            }
            cnts[i][nums[i]]++;
            // cout << i << " : ";
            // for(auto e : cnts[i]) cout << e.first << ":" <<e.second << ' ';

            tot += cnts[i][k];
        }
        return tot;
    }
};
