class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        long long ans = 0;
        unordered_map<int, int> cnt;
        for (int i = 0; i < arr.size(); i++) {
            int x = arr[i];
            for (int j = i - 1; j >= 0 && (arr[j] & x) != arr[j]; j--) {
                arr[j] &= x;
            }
           auto d = upper_bound(arr.begin(), arr.begin() + i + 1, k) - arr.begin();
           auto l = lower_bound(arr.begin(), arr.begin() + i + 1, k) - arr.begin();
           //cout <<l << ' ' <<  d << endl;
           ans += d - l;
        }
        return ans;
    }
};
