/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
    const size = nums.length;
    let total = 0;
    let previousResults = new Map();

    for (let index = 0; index < size; index++) {
        let currentResults = new Map();

        if (nums[index] === k) {
            total++;
        }

        currentResults.set(nums[index], 1);

        previousResults.forEach((frequency, value) => {
            let newAndResult = value & nums[index];

            if (newAndResult === k) {
                total += frequency;
            }

            if (currentResults.has(newAndResult)) {
                currentResults.set(newAndResult, currentResults.get(newAndResult) + frequency);
            } else {
                currentResults.set(newAndResult, frequency);
            }
        });

        previousResults = currentResults;
    }

    return total;
};
