#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll count = 0 ,n = nums.size();

        unordered_map<ll, ll> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> currResults; 

            if (nums[i] == k) ++count;

            currResults[nums[i]] = 1;

            for (auto& x : mp) {
                ll newAndResult = x.first & nums[i];

                if (newAndResult == k) count += x.second;

                currResults[newAndResult] += x.second;
            }

            mp = currResults;
        }

        return count;

    }
};
