class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        subarrs=0
        prev= defaultdict(int)

        for i in nums:
            curr=defaultdict(int)
            if i==k:
                subarrs+=1
            curr[i]+=1

            for val,freq in prev.items():
                andOpr=val&i
                if andOpr==k:
                    subarrs+=freq
                curr[andOpr]+=freq

            prev=curr

        return subarrs
