class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;
        unordered_map<long long, long long> dp;

        for (int i = 0; i < nums.size(); i++) {
            unordered_map<long long, long long> c; 
            if (nums.at(i) == k) {
                res++;
            }
            c[nums.at(i)] = 1;
            for (auto& [v, count] : dp) {
                long long temp = v & nums.at(i);
                if (temp == k) {
                    res += count;
                }
                c[temp] += count;
            }

            dp = c;
        }
        return res;
    }
};
