#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        vector<int>v;
        long long msk = 1;
        for (int i = 1; i <= 32; i++)
            msk *= 2;
        msk--;

        int n = nums.size();
        ll ans = 0ll;

        map<ll, ll> ps,temp;

        for (int j = 0; j < n; j++) {
            temp.clear();

            if (nums[j] == k) {
                ans++;

            }
            temp[nums[j]] = 1ll;

            for (auto [x, y] : ps) {
                ll calc = x & nums[j];
                if (calc == k) {
                    ans += y;

                }
                temp[calc] += y;

            }

            ps = temp;

        }

        return ans;


    }
};
