class Solution {
public:
    long long countSubarrays(vector<int>& snums, int k) {
        int n = snums.size();

        long long scnt = 0;

        unordered_map<long long, long long> smap;

        for (int index = 0; index < n; ++index) 
        {
            unordered_map<long long, long long> ssmap; 

            if (snums[index] == k) 
            {
                ++scnt;
            }
            ssmap[snums[index]] = 1;
            for (auto it : smap) 
            {
                long long snew = it.first & snums[index];

                if (snew == k) {
                    scnt += it.second;
                }

                ssmap[snew] += it.second;
            }

            smap = ssmap;
        }
        return scnt;
    }
};
