class Solution {
public:
    long long countSubarrays(vector<int>& A, int k) {
    int n=A.size();
    long long count=0;
    unordered_map<long long, long long>p;

    for (int i=0;i<n;++i) {
        unordered_map<long long, long long>c; 
        c[A[i]]=1;
        count+=(A[i]==k);
        for(auto pr:p){
            long long cur=pr.first&A[i];
            if(cur==k){
                count+=pr.second;
            }
            c[cur]+=pr.second;
        }  
        p=c;
    }

    return count;
    }
};
