class Solution {
public:
#define ll long long
    long long countSubarrays(vector<int>& nums, int target) {

        int n = nums.size();
        ll result = 0;

        unordered_map<ll, ll> previous;
         ll i=0;
        while(i<n) {

            unordered_map<ll, ll> map; 
            if (nums[i] == target) {
                ++result;
            }
            map[nums[i]] = 1;

            for (auto& [val, freq] : previous) {
                ll andResult = val & nums[i];
                if (andResult == target) {
                    result += freq;
                }
                map[andResult] += freq;
            }

            previous = map;
            i++;
        }
        return result;
    }
};
