class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        size = len(nums)
        total = 0
        previousResults = defaultdict(int)

        for index in range(size):
            currentResults = defaultdict(int)

            if nums[index] == k:
                total += 1

            currentResults[nums[index]] += 1

            for value, frequency in previousResults.items():
                newAndResult = value & nums[index]
                if newAndResult == k:
                    total += frequency
                currentResults[newAndResult] += frequency

            previousResults = currentResults

        return total
