class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        long long cnt = 0;
        unordered_map<long long,long long> m;
        for(int i =0;i<n;i++){
            unordered_map<long long,long long> x;
            if(a[i]==k)cnt++;
            x[a[i]]++;
            for(auto it:m){
                if((it.first&a[i])==k){
                    cnt+=it.second;
                }
                x[(it.first&a[i])]+=it.second;
            }
            m = x;
        }
        return cnt;
    }
};
