class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long value = 0;

        unordered_map<long long, long long> pehleka;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> abhika; 

            if (arr[i] == k) {
                ++value;
            }
            abhika[arr[i]] = 1;

            for (auto it = pehleka.begin(); it != pehleka.end(); ++it) {
                auto& val = it->first;
                auto& hz = it->second;

                long long andwala = val & arr[i];
                if (andwala == k) {
                    value += hz;
                }
                abhika[andwala] += hz;
            }


            pehleka = abhika;
        }

        return value;
    }
};
