class RangeQuery:
    def __init__(self, data, func=min):
        self.func = func
        self._data = _data = [list(data)]
        i, n = 1, len(_data[0])
        while 2 * i <= n:
            prev = _data[-1]
            _data.append([func(prev[j], prev[j + i]) for j in range(n - 2 * i + 1)])
            i <<= 1

    def query(self, start, stop):
        """func of data[start, stop)"""
        depth = (stop - start).bit_length() - 1
        return self.func(self._data[depth][start], self._data[depth][stop - (1 << depth)])

    def __getitem__(self, idx):
        return self._data[0][idx]
class Solution:
    def countSubarrays(self, a: List[int], k: int) -> int:
        s = RangeQuery(a, lambda x, y: x & y)
        ans = 0
        n = len(a)
        for i in range(n):
            left = i
            right = i
            L = i
            U = n - 1
            while L <= U:
                m = (L + U) >> 1
                if(s.query(i, m + 1) <= k):
                    left = m
                    U = m - 1
                else:
                    L = m + 1
            L = i
            U = n - 1
            while L <= U:
                m = (L + U) >> 1
                if(s.query(i, m + 1) >= k):
                    right = m
                    L = m + 1
                else:
                    U = m -1
            if(s.query(i, left + 1) == k):
                ans += right - left + 1
        return ans
