class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<int, int> f;
        for (int x : nums) {
            unordered_map<int, int> f_nxt;
            if ((x & k) == k) f_nxt[x]++;
            for (auto [and_res , cnt] : f) {
                if ((and_res & x & k) == k) f_nxt[and_res & x] += cnt;
            }
            ans += f_nxt[k];
            f = std::move(f_nxt);
        }
        return ans;
    }
};
