class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n=nums.size(),ans=0;
        map<long long ,long long>m;
        for(int i=0;i<n;i++){
             map<long long ,long long>mi;
            if(nums[i]==k) ans++;
            mi[nums[i]]=1;
            for(auto j:m){
                long long x=j.first&nums[i];
                if(x==k) ans+=j.second;
                mi[x]+=j.second;
            }
            m=mi;
        }
        return ans;
    }
};
