func countSubarrays(nums []int, k int) int64 {
	//n := len(nums)
	res := int64(0)
	g := make(map[int]int64)
	for _, v := range nums {
		tmp := make(map[int]int64)
		for key, val := range g {
			if v&key == k {
				res += val
			}
			tmp[v&key] += val
		}
		if v == k {
			res++
		}
		tmp[v]++
		g = tmp
	}
	return res

}
