class Solution:
    def countSubarrays(self, arr: List[int], k: int) -> int:
        st=[]
        res=0
        for i,a in enumerate(arr):
            for j in range(len(st)):
                st[j][0]=st[j][0]&a
            nst=[]
            for v,j in st:
                if len(nst)==0 or v!=nst[-1][0]:  #去重，相同的只留一个
                    nst.append([v,j])
            nst.append([a,i])           #新进的放末尾，先别急着合并
            #res=(res+a)%mod             #新进的答案单独累加
            if a==k:
                res+=1
            for j in range(len(nst)-1):
                v,s,e=nst[j][0],nst[j][1],nst[j+1][1]-1  #每相邻的两项，得到的是一个连续的区间
                if v==k:
                    res+=(e-s+1)

                #res=(res+(e-s+1)*v)%mod                  #这个连续区间里的所有后缀答案都是v
            st=nst
            if len(st)>1 and st[-1][0]==st[-2][0]:       #最后再去重
                st.pop()
            #print(st,res)
        return res
