class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long b = 0;

        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> v; 

            if (nums[i] == k) {
                ++b;
            }
            v[nums[i]] = 1;

            for (auto& [val, freq] : p) {
                long long x = val & nums[i];
                if (x == k) {
                    b += freq;
                }
                v[x] += freq;
            }

            p = v;
        }

        return b;
    }
};
