class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> c(32, 0);

        long long a = 0;

        for(int i = 0; i<n; i++){
            for(int j = 0; j<31; j++){

                if(nums[i]&(1ll<<j)){
                    c[j]++;
                } else {
                    c[j] = 0;
                }
            }

            int maxi = INT_MIN, mini = INT_MAX;
            for(int j = 0; j<31; j++){
                if(k&(1ll<<j)){
                    mini = min(mini, c[j]);
                } else {
                    maxi = max(maxi, c[j]);
                }
            }

            if(k == 0){
                a += (i-maxi+1);
                continue;
            }

            if(mini <= maxi){
                continue;
            }

            a += (mini-maxi);
        }

        return a;
    }
};
