class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long result = 0;
        unordered_map<long long, long long> prevMap;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currMap; 
            if (nums[i] == k) {
                ++result;
            }
            currMap[nums[i]] = 1;
            for (auto& [key, count] : prevMap) {
                long long andResult = key & nums[i];
                if (andResult == k) {
                    result += count;
                }
                currMap[andResult] += count;
            }
            prevMap = currMap;
        }
        return result;
    }
};
