#include <vector>
#include <map>
#include <iostream>
using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& aa, int kk) {
        int nn = aa.size();
        long long dp = 0;
        map<long long, long long> forest;

        for (int i = 0; i < nn; ++i) {
            long long current = aa[i];
            if (current == kk) {
                ++dp;
                // printVal();
            }

            map<long long, long long> currentResults;
            currentResults[current] = 1;

            for (auto& [value, frequency] : forest) {
                long long newAndResult = value & current;
                if (newAndResult == kk) {
                    dp += frequency;
                    // printVal();
                }
                currentResults[newAndResult] += frequency;
            }

            forest = currentResults;
            // readVal(dp); // Print dp value after each iteration
        }

        return dp;
    }

    void readVal(long long val) {
        cout << "Read Value: " << val << endl;
    }

    void printVal() {
        cout << "Performing intermediate processing..." << endl;
    }
};
