class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n=nums.size();
       long long ans=0;
        unordered_map<long long,long long>umap;
        for(int i=0;i<n;i++){
             unordered_map<long long,long long>umap2;
            if(nums[i]==k){
                ans++;
            }
            umap2[nums[i]]++;
            for(auto it:umap){
             long long newand=it.first&nums[i];
             if(newand==k){
                ans+=it.second;
             }
             umap2[newand]+=it.second;   
            }
            umap=umap2;
        }
        return ans;
    }
};
