#define ll long long
#pragma GCC optimize("O3", "unroll-loops")
class Solution {
public:
    Solution(){
        ios_base::sync_with_stdio(false);
        cin.tie(0);
        cout.tie(0);
    }
    long long countSubarrays(vector<int>& nums, int k) {
        int n= nums.size();
        ll ans= 0;
        map<ll, ll> prev;
        for (int i=0;i<n;i++) {
            map<ll, ll> curr; 
            curr[nums[i]]++;
            if (nums[i] == k) {
                ans++;
            }
            for (auto pr : prev) {
                ll val= pr.first&nums[i];
                if (val==k) {
                    ans += pr.second;
                }
                curr[val] += pr.second;
            }
            prev=curr;
        }
        return ans;
    }
};
