class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<long long, long long> prev;
        for (int i=0;i<nums.size();i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ans++;
            }
            curr[nums[i]] = 1;
            for (auto it : prev) {
                long long Res = it.first & nums[i];
                if (Res == k) ans += it.second;
                curr[Res] += it.second;
            }
            prev = curr;
        }
        return ans;
    }
};
